import React from 'react'

const Input = () => {
    return(
        <div>
         <input
            placeholder='type a message'
            value={message}
            onChange={(event) => setMessage(event.target.value)}
            onKeyPress = {event => event.key === 'Enter' ? sendMessage(event) : null }
            >
        </input>
        <i className="fas fa-share"></i>
        </div>
    )
}

export default Input
